import React, { useState, useEffect } from "react";
import "./BrandPortfolio.css";

const BrandPortfolio = () => {
  // State to hold brand data
  const [brands, setBrands] = useState([]);

  // Simulate fetching brand data
  useEffect(() => {
    const fetchBrands = async () => {
      const topBrands = [
        { id: 1, name: "H&M",  src: "../assets/images/brand1.jpg", description: "Trendy and affordable clothing." },
        { id: 2, name: "Levi's", logo: "https://via.placeholder.com/150", description: "Premium denim and casual wear." },
        { id: 3, name: "Nike", logo: "https://via.placeholder.com/150", description: "Leading sportswear and footwear brand." },
        { id: 4, name: "Adidas", logo: "https://via.placeholder.com/150", description: "Stylish sports gear and apparel." },
        { id: 5, name: "Zara", logo: "https://via.placeholder.com/150", description: "High-street fashion for all." },
        { id: 6, name: "Puma", logo: "https://via.placeholder.com/150", description: "Innovative sportswear and shoes." },
        { id: 7, name: "Biba", logo: "https://via.placeholder.com/150", description: "Elegant ethnic wear for women." },
        { id: 8, name: "FabIndia", logo: "https://via.placeholder.com/150", description: "Traditional Indian crafts and clothing." },
        
      ];

      // Simulating an API call delay
      setTimeout(() => {
        setBrands(topBrands);
      }, 1000);
    };

    fetchBrands();
  }, []);

  return (
    <div className="portfolio-container">
      <h1>Top Clothing Brands </h1>
      {brands.length === 0 ? (
        <p>Loading brands...</p>
      ) : (
        <div className="brands-grid">
          {brands.map((brand) => (
            <div key={brand.id} className="brand-card">
              <img src={brand.logo} alt={`${brand.name} logo`} className="brand-logo" />
              <h2>{brand.name}</h2>
              <p>{brand.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BrandPortfolio;
