import React, { useState } from "react";
import "./ImageSlider.css"; // Add custom styling for the slider

const images = [
  "https://via.placeholder.com/800x300?text=Slide+1",
  "https://via.placeholder.com/800x300?text=Slide+2",
  "https://via.placeholder.com/800x300?text=Slide+3",
  "https://via.placeholder.com/800x300?text=Slide+4",
  "https://via.placeholder.com/800x300?text=Slide+5",
];

const ImageSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Go to the next slide
  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Go to the previous slide
  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slider-container">
      {/* Previous Button */}
      <button className="slider-btn prev-btn" onClick={prevSlide}>
        &#10094;
      </button>

      {/* Image Display */}
      <div className="slider">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slide ${index === currentIndex ? "active" : ""}`}
          >
            {index === currentIndex && (
              <img src={image} alt={`Slide ${index + 1}`} />
            )}
          </div>
        ))}
      </div>

      {/* Next Button */}
      <button className="slider-btn next-btn" onClick={nextSlide}>
        &#10095;
      </button>

      {/* Dots for Navigation */}
      <div className="dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? "active-dot" : ""}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
