import React, { useState, useEffect } from "react";
import "./CategoryToBag.css";

const CategoryToBag = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [items, setItems] = useState([]);
  const [bag, setBag] = useState([]);

  // Simulating fetching data
  useEffect(() => {
    const fetchCategories = () => {
      const categoryData = [
        { id: 1, name: "Men" },
        { id: 2, name: "Women" },
        { id: 3, name: "Kids" },
        { id: 4, name: "Accessories" },
      ];
      setCategories(categoryData);
    };

    fetchCategories();
  }, []);

  // Fetch items for the selected category
  useEffect(() => {
    if (selectedCategory) {
      const fetchItems = () => {
        const itemsData = {
          1: [
            { id: 101, name: "Men's T-Shirt", price: 499 },
            { id: 102, name: "Men's Jeans", price: 1499 },
          ],
          2: [
            { id: 201, name: "Women's Dress", price: 1299 },
            { id: 202, name: "Women's Top", price: 799 },
          ],
          3: [
            { id: 301, name: "Kids' T-Shirt", price: 399 },
            { id: 302, name: "Kids' Shorts", price: 499 },
          ],
          4: [
            { id: 401, name: "Watch", price: 1999 },
            { id: 402, name: "Sunglasses", price: 999 },
          ],
        };

        setItems(itemsData[selectedCategory] || []);
      };

      fetchItems();
    }
  }, [selectedCategory]);

  // Add item to the bag
  const addToBag = (item) => {
    setBag([...bag, item]);
  };

  return (
    <div className="container">
      <h1>Myntra Categories</h1>
      <div className="categories">
        {categories.map((category) => (
          <button
            key={category.id}
            className={`category-btn ${
              selectedCategory === category.id ? "active" : ""
            }`}
            onClick={() => setSelectedCategory(category.id)}
          >
            {category.name}
          </button>
        ))}
      </div>

      <div className="items">
        <h2>Items</h2>
        {items.length === 0 ? (
          <p>Select a category to view items.</p>
        ) : (
          items.map((item) => (
            <div key={item.id} className="item-card">
              <p>{item.name}</p>
              <p>₹{item.price}</p>
              <button onClick={() => addToBag(item)}>Add to Bag</button>
            </div>
          ))
        )}
      </div>

      <div className="bag">
        <h2>Shopping Bag</h2>
        {bag.length === 0 ? (
          <p>Your bag is empty.</p>
        ) : (
          bag.map((item, index) => (
            <div key={index} className="bag-item">
              <p>{item.name}</p>
              <p>₹{item.price}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CategoryToBag;
